import React from 'react';


function App(){
  //new/current date formatyear, month, day, hours, minutes, seconds, milliseconds

        let curHr = new Date(1990,5,5,24).getHours();
        let greeting = "";
        const cssStyle = {};
        
        if (curHr < 12) {
          greeting = "Good Morning";
          cssStyle.color = "green";
        } else if (curHr < 18) {
          greeting = "Good Afternoon";
          cssStyle.color = "orange";
        } else {
          greeting = "Good Night";
          cssStyle.color = "black";
        }

        return(
            <div>
            <h1>
              Hello Sir, <span style={cssStyle}> {greeting}</span>{" "}
            </h1>
          </div>
        )
    
}

export default App;
