const App = () => {
  return (
    <>
      <h1 className="heading_style">
        Welcome to{" "}
        <span style={{ fontWeight: "bold" }}> Slot machine game</span>{" "}
      </h1>


      {((x === y) && (x === z))? <Matching/> : <NotMatching/>}


      
      <SlotM x="@" y="@" z="@" />
      <hr />
      <SlotM x="$" y="#" z="#" />
      <hr />
      <SlotM x="!" y="!" z="!" />
      <hr />
      <SlotM x="$" y="$" z="$" />
    </>
  );
};

export default App;
