import React from "react";
import Matching from "./Matching";
import NotMatching from "./NotMatching";
// import SlotM from "./SlotMach";

const SlotM = (props) => {
  //   let x = "@";
  //   let y = "@";
  //   let z = "@";

  let x = props.x;
  let y = props.y;
  let z = props.z;

  if ((x === y) && (x === z)) {
    return <Matching />;
  } else {
    return <NotMatching />;
  }
};
const App = () => {
  return (
    <>
      <h1 className="heading_style">
        Welcome to{" "}
        <span style={{ fontWeight: "bold" }}> Slot machine game</span>{" "}
      </h1>


      {((x === y) && (x === z))}


      
      <SlotM x="@" y="@" z="@" />
      <hr />
      <SlotM x="$" y="#" z="#" />
      <hr />
      <SlotM x="!" y="!" z="!" />
      <hr />
      <SlotM x="$" y="$" z="$" />
    </>
  );
};

export default App;
