import React from "react";
import Matching from "./Matching";
import NotMatching from "./NotMatching";
// import SlotM from "./SlotMach";

const SlotM = (props) => {
  //   //   let x = "@";
  //   //   let y = "@";
  //   //   let z = "@";

  let x = props.x;
  let y = props.y;
  let z = props.z;

  if (x === y && x === z) {
    return (
      <>
        <h1>
          {x} {y} {z}
        </h1>
        <h1> This is Matching :) </h1>
        <hr />
      </>
    );
  } else {
    return (
      <>
        <h1>
          {x} {y} {z}
        </h1>
        <h1> This is not Matching :( </h1>
        <hr />
      </>
    );
   
  }
};

// return{(x === y && x === z)? <Matching/> : <NotMatching/>}

const App = () => {
  return (
    <>
      <h1 className="heading_style">
        Welcome to{" "}
        <span style={{ fontWeight: "bold" }}> Slot machine game</span>{" "}
      </h1>

      <SlotM x="@" y="@" z="@" />
      <hr />
      <SlotM x="$" y="#" z="#" />
      <hr />
      <SlotM x="!" y="!" z="!" />
      <hr />
      <SlotM x="$" y="$" z=":" />
    </>
  );
};

export default App;
